const Search = () =>
{
const [query, setQuery] = useState("");
<div className="input-group rounded">
                <input
                  type="search"
                  className="form-control rounded"
                  placeholder="Search"
                  aria-label="Search"
                  aria-describedby="search-addon"
                  onChange={(event) => setQuery(event.target.value.toLowerCase()) 
                  }
                />
              </div>
}
export default Search;
